        <Route path="/orders" component={Order} />
        <Route path="/product" component={Product} />
        <Route path="/product/add" component={Product} />
        <Route path="/product/edit/:id" component={Product} />

        <Route component={NotFound} />
      </Switch>
    );
  }
  render() {
