import React, { Component } from "react";
import { reduxForm, Field } from "redux-form";
import { FormField } from "../common/FormField";
class ProductForm extends Component {
  rederFields() {
    const formFields = [
      {
        label: "Product Name",
        name: "productName",
        type: "text",
      },
      {
        lable: "Unit Price",
        name: "unitPrice",
        type: "number",
      },
      {
        lable: "Thumbnail",
        name: "thumbnail",
        type: "text",
      },
    ];
    return formFields.map(({ lable, name, type }) => {
      return <Field name={name} type={type} component="input" />;
    });
  }
  render() {
    return (
      <div>
        <form>{this.rederFields()}</form>
      </div>
    );
  }
}
ProductForm = reduxForm({ form: "ProductForm" })(ProductForm);
export default ProductForm;
