import React, { Component } from "react";
import axios from "axios";

export default class Product extends Component {
  constructor(props) {
    super(props);
    this.state = { products: [] };
  }
  componentDidMount() {
    axios.get(" http://localhost:3001/products").then((res) => {
      this.setState({ products: res.data });
      console.log(this.state.products);
    });
  }
  showProduct() {
    if (this.state.products && this.state.products.length != 0) {
      return this.state.products.map((item) => {
        return (
          <div
            key={item.productId}
            className="d-flex flex-col justify-cen style-box"
          >
            <img
              style={style.sizeImg}
              src={process.env.PUBLIC_URL + item.productImg}
            ></img>
            <p className="text-foot w-600" style={style.textHead}>
              {item.productName}
            </p>
            <p className="text-foot w-300" style={style.textPrice}>
              {item.unitPrice} บาท
            </p>
            <button
              style={style.sizeBut}
              value={unitPrice}
              //   onClick={() => this.props.onAddOrder(this.props.product)}
            >
              สั่งซื้อ
            </button>
          </div>
        );
      });
    } else {
      console.log("eiei");
    }
  }
  render() {
    return <div className="d-flex">{this.showProduct()}</div>;
  }
}
