    //     <p>{product.totalPrice}</p>
    //   </div>
    // ));
    const style = {
      sizeImg: { width: 200, height: 220 },
      sizeBut: {
        width: "100%",
        height: 30,
        color: "white",
        marginBottom: 15,
        borderRadius: 15,
        borderStyle: "none",
        backgroundColor: "rgba(20, 170, 20, 0.726)",
      },
      textHead: { margin: 0 },
      textPrice: { alignSelf: "flex-end", fontSize: 15, marginBottom: 5 },
    };
    if (this.state.order.length !== 0) {
      return this.state.order.map((product) => (
        // <div className="box-order">
        //   <div key={product.id}>
        //     <p>{product.totalPrice}</p>
        //   </div>
        // </div>
        <div className="d-flex flex-col justify-cen style-box">
          <img
            style={style.sizeImg}
            src={process.env.PUBLIC_URL + product.orders.productImg}
          ></img>
          <p className="text-foot w-600" style={style.textHead}>
            {product.orders.productName}
          </p>
          <p className="text-foot w-300" style={style.textPrice}>
            {product.orders.unitPrice} บาท
          </p>
          {/* <button
            style={style.sizeBut}
            value={unitPrice}
            onClick={() => this.props.onAddOrder(this.props.product)}
          >
            สั่งซื้อ
          </button> */}
        </div>
      ));
    } else {
      console.log("eiei");
    }
  }
  render() {
    return <div className="d-flex ">{this.showOrders()}</div>;
  }
}
