import React, { Component } from "react";
import axios from "axios";

export default class Product extends Component {
  constructor(props) {
    super(props);
    this.state = { products: [] };
  }
  componentDidMount() {
    axios.get(" http://localhost:3001/products").then((res) => {
      this.setState({ products: res.data });
      console.log(this.state.products);
    });
  }
  delProduct(item) {
    axios
      .delete("http://localhost:3001/products/" + item.productId)
      .then((res) => {
        //   axios.get("http://localhost:3001/products").then((res) => {
        //     this.setState({ products: res.data });
        //   });
        console.log(res);
      });
  }
  showProduct() {
    const style = {
      sizeImg: { width: 200, height: 220 },
      sizeBut: {
        width: "100%",
        height: 30,
        color: "white",
        marginBottom: 15,
        borderRadius: 15,
        borderStyle: "none",
        backgroundColor: "tomato",
      },
      textHead: { margin: 0 },
      textPrice: { alignSelf: "flex-end", fontSize: 15, marginBottom: 5 },
    };
    if (this.state.products && this.state.products.length != 0) {
      return this.state.products.map((item) => {
        return (
          <div
            key={item.productId}
            className="d-flex flex-col justify-cen style-box"
          >
            <img
              style={style.sizeImg}
              src={process.env.PUBLIC_URL + item.productImg}
            ></img>
            <p className="text-foot w-600" style={style.textHead}>
              {item.productName}
            </p>
            <p className="text-foot w-300" style={style.textPrice}>
              {item.unitPrice} บาท
            </p>
            <button
              style={style.sizeBut}
              value={item.unitPrice}
              onClick={() => this.delProduct(item)}
            >
              ลบสินค้า
            </button>
          </div>
        );
      });
    } else {
      console.log("eiei");
    }
  }
  render() {
    return <div className="d-flex">{this.showProduct()}</div>;
  }
}
