import React, { Component } from "react";

export default class Productitem extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    const style = { sizeImg: { width: 80, height: 100 } };
    const { productName, unitPrice, productImg } = this.props;
    return (
      <div className="d-flex">
        <img
          style={style.sizeImg}
          src={process.env.PUBLIC_URL + productImg}
        ></img>
        <p>{productName}</p>
        <p>{unitPrice}</p>
        <button>สั่งซื้อ</button>
      </div>
    );
  }
}
