import React, { Component } from "react";

export default class Productitem extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    const style = {
      sizeImg: { width: 100, height: 120 },
      sizeBut: { width: "100%", height: "100%", borderRadius: 25 },
    };
    const { productName, unitPrice, productImg } = this.props;
    return (
      <div className="d-flex flex-col justify-cen style-box">
        <img
          style={style.sizeImg}
          src={process.env.PUBLIC_URL + productImg}
        ></img>
        <p>{productName}</p>
        <p>{unitPrice} บาท</p>
        <button style={style.sizeBut}>สั่งซื้อ</button>
      </div>
    );
  }
}
