import React, { Component } from "react";
import "../../App.css";
import axios from "axios";
export default class Order extends Component {
  constructor(props) {
    super(props);
    this.state = { order: [] };
  }
  componentDidMount() {
    axios.get("http://localhost:3001/orders").then((res) => {
      {
        this.setState({ order: res.data });
      }
    });
  }
  showOrders() {
    // this.state.orders.map((item) => {
    //   return <div key={item.id}>{item.totalPrice}</div>;
    // });
    // this.state.orders.map((product) => (
    //   <div key={product.id}>
    //     <p>{product.totalPrice}</p>
    //   </div>
    // ));

    if (this.state.order.length !== 0) {
      return this.state.order.map((order) => {
        return (
          <div className="box-order d-flex flex-col">
            <button style={{ alignSelf: "flex-end" }}>x</button>
            <p>{order.orderDate}</p>
            <ul>
              {order.orders &&
                order.orders.map((item) => {
                  return (
                    <li>
                      {item.product.productName} x {item.quatity} ={" "}
                      {item.quatity * item.product.unitPrice}
                    </li>
                  );
                })}
            </ul>
            <p>ราคารวม {order.totalPrice}</p>
          </div>
        );
      });
    } else {
      console.log("eiei");
    }
  }
  render() {
    return <div className="d-flex ">{this.showOrders()}</div>;
  }
}
